const Button = () => {
  const [showNotice, setShowNotice] = React.useState(false)
  const Show = () => setShowNotice(true)
  return (
    <div className="salary" onClick={Show} data-title="薪水查詢">
        <img src={Salary} alt="Salary" />
        <h3>薪水查詢</h3>
       { showNotice ? <Notice />: null }
    </div>
  )
}
