import React, { Component } from 'react'
import Salary from './images/button02.png';


// function showNotice(){
//   document.getElementsByClassName('notice').show();
// }
const Search = () => {
  const [showNotice, setShowNotice] = React.useState(false)
  const onClick = () => setShowNotice(true)
  return (
    <div>
      <input type="submit" value="Search" onClick={onClick} />
      { showNotice ? <Results /> : null }
    </div>
  )
}

const Results = () => (
  <div id="results" className="search-results">
    Some Results
  </div>
)

class Button extends Component {
    constructor() {
      super()
      this.showNotice = this.showNotice.bind(this)
    }
    showNotice() {
        // let salary = document.getElementsByClassName('salary');
        // salary.style.display='none';
    }
    render() {
      return (          
        <div className="salary" onClick={this.showNotice} data-title="薪水查詢">
            <img src={Salary} alt="Salary" />
            <h3>薪水查詢</h3>
        </div>
      )
    }
  }


export default Button
